import React, { useState, useEffect, useRef } from 'react';
import { TrendingUp, History, Info, Trophy, Flame, Skull, Crown } from 'lucide-react';
import { Button } from '../../UI/Button';
import Card from '../../UI/Card';
import FeeTooltip from '../../UI/FeeTooltip';
import gsap from 'gsap';

// DEGEN usernames and their pfp URLs
const DEGEN_USERS = [
  { name: "GIGACHAD.sol", pfp: "/images/gigachad.png" },
  { name: "wojak.eth", pfp: "/images/wojak.png" },
  { name: "DumpItPamp", pfp: "/images/pepe.png" },
  { name: "MoonBoi420", pfp: "/images/doge.png" },
  { name: "LaserEyesMAXI", pfp: "/images/laser-eyes.png" },
  { name: "NGMI_Trader", pfp: "/images/crying-wojak.png" },
  { name: "BonkMaster", pfp: "/images/bonk.png" },
  { name: "RUG.SOL", pfp: "/images/rug.png" }
];

interface LeaderboardEntry {
  user: typeof DEGEN_USERS[0];
  multiplier: number;
  amount: number;
  timestamp: number;
}

interface GameHistoryEntry {
  multiplier: number;
  color: string;
  timestamp: number;
}

const CrashGame: React.FC = () => {
  const [multiplier, setMultiplier] = useState(1.00);
  const [isPlaying, setIsPlaying] = useState(false);
  const [betAmount, setBetAmount] = useState(1);
  const [autoCashout, setAutoCashout] = useState(2);
  const [gameHistory, setGameHistory] = useState<GameHistoryEntry[]>(() => {
    const saved = localStorage.getItem('crashGameHistory');
    return saved ? JSON.parse(saved) : [];
  });
  const [leaderboard] = useState<LeaderboardEntry[]>([
    { user: DEGEN_USERS[0], multiplier: 69.42, amount: 4.20, timestamp: Date.now() - 120000 },
    { user: DEGEN_USERS[1], multiplier: 42.0, amount: 6.9, timestamp: Date.now() - 300000 },
    { user: DEGEN_USERS[2], multiplier: 37.5, amount: 2.5, timestamp: Date.now() - 600000 },
    { user: DEGEN_USERS[3], multiplier: 28.8, amount: 1.5, timestamp: Date.now() - 900000 },
    { user: DEGEN_USERS[4], multiplier: 25.2, amount: 3.3, timestamp: Date.now() - 1200000 }
  ]);

  const animationRef = useRef<number>();
  const lastUpdateRef = useRef<number>();
  const multiplierRef = useRef<HTMLDivElement>(null);
  const resultTextRef = useRef<HTMLDivElement>(null);
  const particlesContainerRef = useRef<HTMLDivElement>(null);
  const shouldAutoCashoutRef = useRef(false);

  useEffect(() => {
    localStorage.setItem('crashGameHistory', JSON.stringify(gameHistory.slice(0, 50)));
  }, [gameHistory]);

  const addToGameHistory = (entry: GameHistoryEntry) => {
    setGameHistory(prev => [entry, ...prev].slice(0, 50));
  };

  const createParticles = (color: string, count: number) => {
    if (!particlesContainerRef.current) return;

    const container = particlesContainerRef.current;
    container.innerHTML = '';

    for (let i = 0; i < count; i++) {
      const particle = document.createElement('div');
      particle.className = 'absolute w-2 h-2 rounded-full';
      particle.style.backgroundColor = color;
      particle.style.left = '50%';
      particle.style.top = '50%';

      const angle = (i / count) * Math.PI * 2;
      const distance = 100 + Math.random() * 100;
      const duration = 0.5 + Math.random() * 0.5;
      const scale = 0.5 + Math.random() * 1.5;

      container.appendChild(particle);

      gsap.fromTo(particle,
        {
          x: 0,
          y: 0,
          scale: 0,
          opacity: 1
        },
        {
          x: Math.cos(angle) * distance,
          y: Math.sin(angle) * distance,
          scale: scale,
          opacity: 0,
          duration: duration,
          ease: 'power2.out',
          onComplete: () => particle.remove()
        }
      );
    }
  };

  const showResultText = (text: string, color: string) => {
    if (!resultTextRef.current) return;

    const resultText = resultTextRef.current;
    resultText.textContent = text;
    resultText.style.color = color;

    gsap.fromTo(resultText,
      {
        scale: 0,
        opacity: 0,
        y: 0
      },
      {
        scale: 1,
        opacity: 1,
        y: -50,
        duration: 0.5,
        ease: 'back.out(1.7)',
        onComplete: () => {
          gsap.to(resultText, {
            opacity: 0,
            y: -100,
            duration: 0.5,
            delay: 1
          });
        }
      }
    );
  };

  const startGame = () => {
    setIsPlaying(true);
    setMultiplier(1.00);
    lastUpdateRef.current = Date.now();
    shouldAutoCashoutRef.current = true;
    animate();

    if (multiplierRef.current) {
      gsap.to(multiplierRef.current, {
        scale: 1,
        opacity: 1,
        duration: 0.3
      });
    }

    showResultText('LFG! ðŸš€', '#FFB800');
  };

  const animate = () => {
    const now = Date.now();
    const delta = (now - (lastUpdateRef.current || now)) / 1000;
    lastUpdateRef.current = now;

    setMultiplier(prev => {
      const newMultiplier = prev + (delta * (prev * 0.5));
      
      if (shouldAutoCashoutRef.current && newMultiplier >= autoCashout && isPlaying) {
        cashout();
        return prev;
      }
      return newMultiplier;
    });

    if (Math.random() < 0.005) {
      crash();
      return;
    }

    animationRef.current = requestAnimationFrame(animate);
  };

  const cashout = () => {
    if (!isPlaying) return;
    
    setIsPlaying(false);
    cancelAnimationFrame(animationRef.current!);
    shouldAutoCashoutRef.current = false;
    
    const winAmount = betAmount * multiplier;
    showResultText(`WAGMI! +${winAmount.toFixed(2)} SOL ðŸš€`, '#28A745');
    createParticles('#28A745', 30);
    
    addToGameHistory({
      multiplier: multiplier,
      color: '#28A745',
      timestamp: Date.now()
    });
    
    if (multiplierRef.current) {
      gsap.to(multiplierRef.current, {
        scale: 1.2,
        opacity: 0,
        duration: 0.5,
        ease: 'power2.out'
      });
    }
  };

  const crash = () => {
    setIsPlaying(false);
    cancelAnimationFrame(animationRef.current!);
    shouldAutoCashoutRef.current = false;
    setMultiplier(1.00);
    
    showResultText('NGMI! ðŸ’€', '#DC3545');
    createParticles('#DC3545', 20);
    
    addToGameHistory({
      multiplier: multiplier,
      color: '#DC3545',
      timestamp: Date.now()
    });
    
    if (multiplierRef.current) {
      gsap.to(multiplierRef.current, {
        scale: 0.5,
        opacity: 0,
        duration: 0.3,
        ease: 'power2.in'
      });
    }
  };

  useEffect(() => {
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  const formatTimeAgo = (timestamp: number) => {
    const seconds = Math.floor((Date.now() - timestamp) / 1000);
    if (seconds < 60) return `${seconds}s ago`;
    if (seconds < 3600) return `${Math.floor(seconds / 60)}m ago`;
    return `${Math.floor(seconds / 3600)}h ago`;
  };

  return (
    <div className="container mx-auto px-4 pt-24 pb-16">
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center gap-4 mb-6">
          <h1 className="text-3xl sm:text-4xl font-bold bg-gradient-to-r from-[#FFB800] via-[#FF3D00] to-[#FF9100] bg-clip-text text-transparent">
            ðŸŽ® DEGEN CRASH
          </h1>
          <div className="px-3 py-1 rounded-full bg-[#FFB800]/20 border border-[#FFB800]/30">
            <span className="text-[#FFB800] text-sm font-medium">LIVE ðŸ”¥</span>
          </div>
        </div>

        <p className="text-xl text-gray-400 mb-8">
          APE IN NOW! Place your bet and watch the multiplier moon! WAGMI fam! ðŸš€
        </p>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
          <div className="lg:col-span-8">
            <Card className="relative h-[600px] flex flex-col items-center justify-center overflow-hidden">
              <div className="absolute inset-0">
                <div className="absolute inset-0 bg-gradient-to-t from-background via-transparent to-transparent" />
                <div className="absolute inset-0 bg-[url('data:image/png;base64,') opacity-5" />
              </div>
              
              <div ref={particlesContainerRef} className="absolute inset-0 pointer-events-none" />
              
              <div ref={resultTextRef} className="absolute text-4xl font-bold z-20 pointer-events-none" />

              <div 
                ref={multiplierRef}
                className="text-8xl font-bold bg-gradient-to-r from-[#FFB800] via-[#FF3D00] to-[#FF9100] bg-clip-text text-transparent mb-4 z-10 relative animate-pulse"
              >
                {multiplier.toFixed(2)}x
              </div>

              {isPlaying ? (
                <Button
                  variant="success"
                  size="lg"
                  onClick={cashout}
                  className="min-w-[200px]"
                >
                  Cash Out ({(betAmount * multiplier).toFixed(2)} SOL)
                </Button>
              ) : (
                <Button
                  variant="premium"
                  size="lg"
                  onClick={startGame}
                  className="min-w-[200px]"
                >
                  APE IN NOW! ðŸš€
                </Button>
              )}
            </Card>

            <Card title="Recent Games" className="mt-6">
              <div className="flex gap-2 overflow-x-auto pb-2">
                {gameHistory.slice(0, 10).map((game, index) => (
                  <div
                    key={index}
                    className="flex-shrink-0 w-20 h-20 rounded-lg flex items-center justify-center"
                    style={{ backgroundColor: `${game.color}20` }}
                  >
                    <div className="flex flex-col items-center">
                      <span className={`text-lg font-bold`} style={{ color: game.color }}>
                        {game.multiplier.toFixed(2)}x
                      </span>
                      <span className="text-xs text-gray-400">
                        {formatTimeAgo(game.timestamp)}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </Card>
          </div>

          <div className="lg:col-span-4 space-y-6">
            <Card title="Place Bet" variant="premium">
              <div className="mb-4">
                <label className="block text-sm text-gray-400 mb-2">
                  Bet Amount (SOL)
                </label>
                <input
                  type="number"
                  min="0.1"
                  step="0.1"
                  value={betAmount}
                  onChange={(e) => setBetAmount(parseFloat(e.target.value))}
                  className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-[#FFB800]/50"
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm text-gray-400 mb-2">
                  Auto Cashout (x)
                </label>
                <input
                  type="number"
                  min="1.01"
                  step="0.01"
                  value={autoCashout}
                  onChange={(e) => setAutoCashout(parseFloat(e.target.value))}
                  className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-[#FFB800]/50"
                />
              </div>

              <div className="flex items-center justify-between mb-4">
                <span className="text-gray-400">Fee (2%)</span>
                <div className="flex items-center">
                  <span className="text-white">{(betAmount * 0.02).toFixed(2)} SOL</span>
                  <FeeTooltip className="ml-2" />
                </div>
              </div>

              <div className="p-4 bg-[#FFB800]/10 border border-[#FFB800]/20 rounded-lg flex items-start">
                <Info size={20} className="text-[#FFB800] mr-3 mt-0.5 flex-shrink-0" />
                <p className="text-sm text-gray-300">
                  The game will automatically cash out when the multiplier reaches your auto cashout value.
                </p>
              </div>
            </Card>

            <Card title="ðŸ† DEGEN Leaderboard" variant="premium">
              <div className="space-y-4">
                {leaderboard.map((entry, index) => (
                  <div key={index} className="flex items-center gap-4 p-3 rounded-lg bg-white/5 border border-white/10">
                    <div className="w-10 h-10 rounded-full overflow-hidden">
                      <img src={entry.user.pfp} alt="" className="w-full h-full object-cover" />
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <span className="font-bold text-white">{entry.user.name}</span>
                        {index === 0 && <Crown size={16} className="text-[#FFB800]" />}
                      </div>
                      <div className="text-sm text-gray-400">{formatTimeAgo(entry.timestamp)}</div>
                    </div>
                    <div className="text-right">
                      <div className="font-bold text-[#28A745]">{entry.multiplier}x</div>
                      <div className="text-sm text-gray-400">{entry.amount} SOL</div>
                    </div>
                  </div>
                ))}
              </div>
            </Card>

            <Card title="ðŸ“Š Statistics" variant="premium">
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-gray-400">Highest Multiplier</span>
                  <span className="text-[#28A745] font-medium">48.2x</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-400">Games Played</span>
                  <span className="text-white font-medium">1,284</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-400">Total Wagered</span>
                  <span className="text-white font-medium">15,420 SOL</span>
                </div>
              </div>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CrashGame;